import { GET_ERRORS, CLEAR_ERRORS } from "../actions/types";
const initialState = {
    msg: "",
    type: "",
    code: 0,
};
export default function (state = initialState, action) {
    switch (action.type) {
        case GET_ERRORS:
            return {
                msg: action.payload.msg,
                type: action.payload.type,
                code: action.payload.code,
            };

        case CLEAR_ERRORS:
            console.log("clearing reducer");
            return null;
        case CLOSE_ERRORS:
            console.log("clearing reducer");
            return null;
        default:
            return state;
    }
}
