import React from "react";
import LoginForm from "../components/smart/LoginForm";
import Link from "next/link";
import { SignUpFacebook, SignUpGoogle } from "../components/dumb/SignUpVia";
import { Auth } from "../customHooks/authMiddleware";
import Layout from "../components/layout";
import styled from "styled-components";
const FormContainer = styled.div`
    height: 600px;
    background: linear-gradient(180deg, #d78383 0%, #a02f2f 100%);
    position: absolute;
    box-shadow: 0px 0px 50px rgba(0, 0, 0, 0.25);
    display: flex;
    flex-direction: column;
    align-items: center;
    right: 0;
    top: 0;
    width: 600px;
    header {
        color: white;
        margin: 40px;
        text-align: center;
        font-size: 1.2rem;
    }
    .p {
        margin-top: 20px;
        color: #e8e8e8;
        a {
            margin-left: 5px;
        }
        a:link,
        a:visited {
            color: white;
        }
        a:hover,
        a:active {
            color: #f2f2f2;
            text-shadow: 0 0 4px rgba(20, 20, 20, 0.2);
        }
    }
`;
const Page = () => {
    Auth(false);
    return (
        <Layout title="Welcome!">
            <FormContainer>
                {" "}
                <header>
                    <h1>Witaj!</h1>
                    <br />
                    <h4>Aby rozpocząć zaloguj się na swoje konto!</h4>
                </header>
                <LoginForm />
                <p className="p">
                    Nie masz konta?
                    <Link href="/register">
                        <a>ZAREJESTRUJ SIĘ!</a>
                    </Link>
                </p>
                <p className="p">
                    <Link href="/recoverpassword">
                        <a>Nie pamiętasz hasła?</a>
                    </Link>
                </p>
                <SignUpGoogle />
                <SignUpFacebook />
            </FormContainer>
        </Layout>
    );
};

export default Page;
