import { GET_ERRORS, CLEAR_ERRORS, CLOSE_ERRORS } from "../actions/types";
const initialState = {
    msg: "",
    type: "",
    code: 0,
};
export default function (state = initialState, action) {
    switch (action.type) {
        case GET_ERRORS:
            return {
                msg: action.payload.msg,
                type: action.payload.type,
                code: action.payload.code,
            };

        case CLOSE_ERRORS:
            return {
                msg: {},
                status: null,
                id: null,
            };
        default:
            return state;
    }
}
