import React, { useRef } from "react";
import styled from "styled-components";
import CrossMark from "../svg/cross.svg";
export const ErrorContainer = styled.div`
    background-color: ${(props) => (props.type === "positive" ? "#90ee90" : "#fc8f8f")};
    margin: 10px 0;
    padding: 5px;
    transition: 0.5s;
    display: flex;
    flex-direction: row;
    align-items: center;
    wdith: 50%;
`;
export const Xdiv = styled.div`
    padding-right: 5px;
    svg {
        height: 15px;
        width: 15px;
        fill: white;
        path {
            fill: black;
        }
    }
`;

const Error = ({ message }) => {
    const container = useRef(null);
    const closeError = () => {
        container.current.remove();
    };
    return (
        <ErrorContainer ref={container}>
            <Xdiv onClick={closeError}>
                <CrossMark />
            </Xdiv>
            <p>Invalid Credentials{message}</p>
        </ErrorContainer>
    );
};

export default Error;
