import React, { useRef } from "react";
import styled from "styled-components";
import CrossMark from "../svg/cross.svg";
export const ErrorContainer = styled.div`
    background-color: ${(props) => (props.type === "positive" ? "#90ee90" : "#fc8f8f")};
    padding: 5px;
    transition: 0.5s;
    display: flex;
    flex-direction: row;
    align-items: center;
    text-align: center;
    width: 75%;
`;
export const Xdiv = styled.div`
    padding-right: 5px;
    svg {
        height: 15px;
        width: 15px;
        fill: white;
        path {
            fill: black;
        }
    }
`;

const Error = ({ message }) => {
    const container = useRef(null);
    const closeError = () => {
        container.current.remove();
    };
    s;
    return (
        <ErrorContainer ref={container}>
            <Xdiv onClick={closeError}>
                <CrossMark />
            </Xdiv>
            <p> {message}</p>
        </ErrorContainer>
    );
};

export default Error;
