import React from "react";
import { useForms } from "../../customHooks/formHook";
import FormItem from "../dumb/formItem";
import SubmitButton from "../dumb/SubmitButton";
import { useDispatch, useSelector } from "react-redux";
import { login } from "../../redux/actions/authActions";
import { returnErrors, clearErrors } from "../../redux/actions/errorActions";
import styled from "styled-components";

import Social from "../svg/social.svg";
import Lock from "../svg/lock.svg";

export const Form = styled.form`
    display: flex;
    flex-direction: column;
    align-items: center;
    & > * {
        margin-top: 30px;
    }
`;

const LoginForm = () => {
    const dispatch = useDispatch();
    const { inputs, handleInputChange } = useForms();
    let isError = false;
    const handleSubmit = (e) => {
        e.preventDefault();
        const { email, password } = inputs;
        const user = {
            email,
            password,
        };
        dispatch(clearErrors());
        dispatch(login(user));
    };

    return (
        <Form onSubmit={handleSubmit} autoComplete="off">
            <FormItem
                type="text"
                value={inputs.email}
                onChange={handleInputChange}
                name="email"
                placeholder="Email"
                svg={<Social />}
            />
            <FormItem
                type="password"
                value={inputs.password}
                onChange={handleInputChange}
                name="password"
                placeholder="Hasło"
                svg={<Lock />}
            />
            <SubmitButton type="submit">Zaloguj się</SubmitButton>
        </Form>
    );
};

export default LoginForm;
