import { GET_ERRORS, CLEAR_ERRORS } from "../actions/types";
const initialState = {
    msg: "",
    type: "",
    code: "",
};
export default function (state = null, action) {
    switch (action.type) {
        case GET_ERRORS:
            return action.payload;

        case CLEAR_ERRORS:
            return [];

        default:
            return state;
    }
}
